mapper.select:
SELECT

FROM z_zeus.users_data A


    a.user_is_cancel = '1'

        AND a.email = #{ue.email}


        AND a.phone_no = #{ue.phoneNo}

SELECT
    COUNT(1)
FROM z_zeus.user_login_record A
WHERE a.user_no = #{userNo}
  AND a.token = #{token}
  AND a.login_out_time IS NULL

mapper.update:
UPDATE z_zeus.user_login_record
   SET login_out_time = now(), login_out_type = #{loginOutType},
       login_out_user = #{loginOutUser}, update_time = now()
 WHERE user_no = #{userNo}
   AND token = #{token}

mapper.select:
SELECT a.user_id as "userId", a.user_relation_id as "userRelationId", a.user_no as "userNo", a.cool_name as "coolName", a.phone_no as "phoneNo", a.email as "email", a.user_firstname as "userFirstname", a.user_name as "userName", a.user_gender as "userGender", a.user_birthday as "userBirthday", a.user_age as "userAge", a.user_constellation as "userConstellation", a.user_blood_type as "userBloodType", a.user_birth_country as "userBirthCountry", a.user_birth_province as "userBirthProvince", a.user_birth_city as "userBirthCity", a.user_birth_county as "userBirthCounty", a.user_now_country as "userNowCountry", a.user_now_province as "userNowProvince", a.user_now_city as "userNowCity", a.user_now_county as "userNowCounty", a.user_is_cancel as "userIsCancel" FROM z_zeus.users_data A
AND a.cool_name = #{coolName}
AND a.user_no = #{userNo}
